mapper.update:
update ${resourceDataDb}
set exist = 0
where resource_data_id = #{resourceDataId}
and workspace_id = #{workspaceId}
and iteration = #{iteration}

mapper.select:
SELECT DISTINCT resource_data_id as resourceDataId,name,max(gmt_create) as gmtCreate,workspace_id as workspaceId
FROM ${resourceDataDb}
where exist = 1
group by resource_data_id,name,workspace_id
having workspace_id=#{workspaceId}
LIMIT #{offset},#{pageSize}

SELECT count(*)
FROM ${resourceDataDb}
where exist = 1
group by resource_data_id,name,workspace_id
having workspace_id=#{workspaceId}

SELECT 
FROM ${resourceDataDb}
WHERE resource_data_id=#{resourceDataId} and workspace_id=#{workspaceId} and iteration=#{iteration}
AND exist = 1
