mapper.update:
update ${resourceDataDb}
set exist = 0
where resource_data_id = #{resourceDateId}
and workspace_id = #{workspaceId}
and iteration in(
    select last_iteration as iteration
    from ${resourceIterationDb}
    where resource_data_id = #{resourceDateId}
    and workspace_id = #{workspaceId}
    )

mapper.select:
SELECT 
FROM ${resourceDataDb}
WHERE (resource_data_id, iteration) IN (
SELECT resource_data_id, last_iteration
FROM ${resourceIterationDb}
WHERE workspace_id = #{workspaceId} 
)
AND exist = 1
LIMIT #{offset},#{pageSize}

SELECT count(*)
FROM ${resourceDataDb}
WHERE (resource_data_id, iteration) IN (
SELECT resource_data_id, last_iteration
FROM ${resourceIterationDb}
WHERE workspace_id = #{workspaceId} 
)
AND exist = 1

SELECT 
FROM ${resourceDataDb}
WHERE (resource_data_id, iteration) IN (
SELECT resource_data_id, last_iteration
FROM ${resourceIterationDb}
WHERE workspace_id = #{workspaceId}
and resource_data_id=#{resourceDateId}
)
AND exist = 1

SELECT 
FROM ${resourceDataDb}
WHERE (resource_data_id, iteration) IN (
SELECT resource_data_id, last_iteration
FROM ${resourceIterationDb}
WHERE workspace_id = #{workspaceId} 
)
AND exist = 1
AND name like concat('%',#{name},'%')
LIMIT #{offset},#{pageSize}

select count(*)
FROM ${resourceDataDb}
WHERE (resource_data_id, iteration) IN (
SELECT resource_data_id, last_iteration
FROM ${resourceIterationDb}
WHERE workspace_id = #{workspaceId} 
)
AND exist = 1
AND name like concat('%',#{name},'%')
